### Start time for code, approximate timeStart <- as.character(Sys.time()) ### code chunk number 2: Load all required libraries library(minfi, quietly=TRUE, warn.conflicts=FALSE,verbose = FALSE) ### code chunk number 3: Update package version if (packageVersion("minfi") < "1.15.6") { stop("Please update 'minfi' to version >= 1.15.7 to run this tool") }
### Parse cheetah code and make dataframe for creating tmp dir minfi_config_path = paste0("$__new_file_path__","/minfi_config.txt") minfi_config = read.table(minfi_config_path) colnames(minfi_config) = c("status","green","red","name")
### Make the tmpdir for symlinking data base_dir = paste0("$__new_file_path__","/base") system(paste0("mkdir ",base_dir))
minfi_config\$name
class(minfi_config)
### Make symlinks of files for (i in 1:nrow(minfi_config)){ stopifnot(nrow(minfi_config) == nrow(minfi_config["name"]))
### Make green idat file symlinks file_green = paste0(base_dir,"/",as.character(minfi_config[i,"name"]),"_Grn.idat") cmd_green = paste("ln -s",as.character(minfi_config[i,"green"]),file_green,sep=" ") cat("Symlink for Green Idat file number ",i," ", cmd_green) system(cmd_green)
### Make red idat file symlinks file_red = paste0(base_dir,"/",as.character(minfi_config[i,"name"]),"_Red.idat") cmd_red = paste("ln -s",as.character(minfi_config[i,"red"]),file_red,sep=" ") cat("Symlink for Red Idat file number ",i," ", cmd_red) system(cmd_red) }
### Make dataframe with Basenames Basename = paste0(base_dir,"/",unique(substr(list.files(base_dir),1,17))) status = minfi_config[match(gsub(".+/","",Basename), minfi_config\$name),"status"] targets = data.frame(Basename=Basename,status=status) cat("Targets file for reading 450K files") targets
### Read 450k files RGset = read.450k.exp(targets=targets,verbose=T)
